		   	jQuery(document).ready(function(){
		    	jQuery('input[id=searchInput]').focus(function() {
		        	if( this.value == this.defaultValue ) {
		            	this.value = "";
		           	}
	        	}).blur(function() {
	            	if( !this.value.length ) {
	                	this.value = this.defaultValue;
	            	}
	        	});
	    	});   

			function soloNumerosLetras(e){
				var key = window.Event ? e.which : e.keyCode;
				return ((key >= 48 && key <= 57) || (key >= 65 && key <= 90) || (key >= 97 && key <= 122)  || (key == 8) || (e.keyCode == 9));
			}

br.script:
		/*
		 * Tipo de Componente: JS
		 * Nombre: versionesNoSoportadasNavegadores.js
		 * Descripción: Variables con la versión no soportada de los navegadores
		 * Autor: Mario Granja
		 * Parametros: ninguno
		 * Valores de retorno: ninguno
		*/


		var VERSION_MINIMA_SOPORTADA_SAFARI = 10;
		var VERSION_MINIMA_SOPORTADA_FIREFOX = 53;
		var VERSION_MINIMA_SOPORTADA_CHROME = 58;
		var VERSION_MINIMA_SOPORTADA_EDGE = 15;


		var ID_BIENVENIDOS = '';
		var ESTA_EN_BIENVENIDOS = false;
		var TIMEOUT_VALIDACION = 1500;

		/*
		 * Tipo de Componente: JS
		 * Nombre: verificarNavegadorPaginaBienvenidos.js
		 * Descripción: Verifica que se use un navegador soportado
		 * Autor: Mario Granja
		 * Parametros: ninguno
		 * Valores de retorno: ninguno
		*/
		navigator.browserSpecs = (function () {
		  var ua = navigator.userAgent, tem,
		    M = ua.match(/(opera|chrome|safari|firefox|msie|trident(?=\/))\/?\s*(\d+)/i) || [];
		  if (/trident/i.test(M[1])) {
		    tem = /\brv[ :]+(\d+)/g.exec(ua) || [];
		    return {name: 'IE', version: (tem[1] || '')};
		  }
		  if (M[1] === 'Chrome') {
		    tem = ua.match(/\b(OPR|Edge)\/(\d+)/);
		    if (tem != null) return {name: tem[1].replace('OPR', 'Opera'), version: tem[2]};
		  }
		  M = M[2] ? [M[1], M[2]] : [navigator.appName, navigator.appVersion, '-?'];
		  if ((tem = ua.match(/version\/(\d+)/i)) != null) M.splice(1, 1, tem[1]);
		  return {name: M[0], version: M[1]};
		})();


		if ((navigator.browserSpecs.name == 'MSIE' || navigator.browserSpecs.name == 'IE')  
		  	|| (navigator.browserSpecs.name == 'Chrome' && Number(navigator.browserSpecs.version) < VERSION_MINIMA_SOPORTADA_CHROME) 
			|| (navigator.browserSpecs.name == 'Firefox' && Number(navigator.browserSpecs.version)< VERSION_MINIMA_SOPORTADA_FIREFOX) 
		  	|| (navigator.browserSpecs.name == 'Safari' && Number(navigator.browserSpecs.version) < VERSION_MINIMA_SOPORTADA_SAFARI)
    	  	|| (navigator.browserSpecs.name == 'Edge' && Number(navigator.browserSpecs.version) < VERSION_MINIMA_SOPORTADA_EDGE)
		) {

		  setTimeout(function () {


		    document.getElementById('noSoportado').style.display = 'block';
		    document.getElementById('disablingDiv').style.display = 'block';
		    document.getElementById('contenidoPantallaLogin').style.display = 'none';


		    if (ESTA_EN_BIENVENIDOS) {
		      document.getElementById(ID_BIENVENIDOS).style.display = 'none';
		    }

		  }, TIMEOUT_VALIDACION);

		}
